package Assi3;

public class fact {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=67;
	    int fact=1;
	    System.out.println("The the number");
	           
	    for(int i=1;i<=n;i++){
	        fact=fact*i;
	    }
	    System.out.println("the factorial of number is "+fact);

	}

}
