package extracode;

public class Series3 {
	//1+22+333+

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;

		int c=1;
	    for(int i=1;i<=n;i++){
	    	
	    	for(int p=1;p<=i;p++) {
	    		
	    		System.out.print(c);
	    		c++;
	    		
	    	}
	    	if(i!=n) {
	    		System.out.print("+");
	    		
	    	}
	        
	    }
	    
	    //1 23 456 
	    //fibona series in pyramide
	    
	}

}
