package extracode;
//A+BB+CCC
public class Series7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		char c='A';
		for(int i=1;i<=n;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print(c);
				
			}
			System.out.print("+");
			c++;
			
		}
		

	}

}
